async function run() {
    let ok = 0, fail = 0;
    for (const [name, d] of Object.entries(infoByName)) {
        const vi = JSON.stringify(d.vi);
        let r;
        if (d.website) {
            r = await pool.query('UPDATE "Museum" SET "visitorInfo"=$1, website=$2 WHERE name=$3 AND "visitorInfo" IS NULL RETURNING id', [vi, d.website, name]);
        } else {
            r = await pool.query('UPDATE "Museum" SET "visitorInfo"=$1 WHERE name=$2 AND "visitorInfo" IS NULL RETURNING id', [vi, name]);
        }
        if (r.rowCount > 0) ok += r.rowCount; else fail++;
    }
    // Count remaining
    const { rows: [c] } = await pool.query('SELECT count(*) as c FROM "Museum" WHERE "visitorInfo" IS NULL');
    console.log(`âœ… ${ok}ê°œ ì—…ë°ì´íŠ¸, âŒ ${fail}ê°œ ì´ë¦„ ë¶ˆì¼ì¹˜, ë‚¨ì€ ë¯¸ìž…ë ¥: ${c.c}ê°œ`);
    pool.end();
}
run().catch(e => { console.error(e); pool.end(); });
